  margin-bottom: 8px;
}

.win31-cue-card-steps {
  padding-left: 16px;
}

.win31-cue-card-nav {
  display: flex;
  justify-content: space-between;
  padding-top: 12px;
  border-top: 1px solid var(--win31-dark-gray);
  margin-top: 12px;
